package dht

import (
	"encoding/hex"
	"testing"

	"github.com/davecgh/go-spew/spew"
	"github.com/lyoshenka/bencode"
)

func TestDecode(t *testing.T) {
	strs := []string{
		"",
		"",
		"",
	}

	for _, str := range strs {
		raw, err := hex.DecodeString(str)
		if err != nil {
			t.Error(err)
			continue
		}

		var decoded interface{}
		err = bencode.DecodeBytes(raw, &decoded)
		if err != nil {
			t.Error(err)
			continue
		}

		t.Error("TODO")
		continue

		spew.Dump(decoded)
	}
}
